<script>
/*
*
*	tab组件
*
*/
var Tab	= function(target){

	this.$target	= $(target);
	this.$nav		= this.$target.find('.tab_nav').find('li');
	this.$content	= this.$target.find('.tab_content').find('li');
	
	this.init	= function(){	

		for(var i = 0;i<this.$nav.length;i++){		
			var t	= $(this.$nav[i]);
			t.on("click",$.proxy(this.showtarget,this,{index:i,target:t}));						
		}
	
	}
	
	this.showtarget	= function(e){

		for(var i = 0;i<this.$nav.length;i++){	
			
			var n	= $(this.$nav[i]);
			var c	= $(this.$content[i]);
			
			if(i == e.index){
			
				n.addClass('selected');
				c.css('display','block');
			
			}else{
			
				n.removeClass('selected');
				c.css('display','none');
			
			}
		}
	}
}

/*
*
*	导航class初始化
*
*/
var NavDomInit	= function(target){
	
	this.suffix	= '-hover';
	this.$target	= $(target);
	this.prefix		= target.replace('.','');
	//this.prefix		= this.prefix == 'fst-cat'?'cat-hd':this.prefix;
	if(this.prefix == 'fst-cat'){
	
		this.prefix	= 'cat-hd';
	
	}else if(this.prefix == 'snd-cat'){
	
		this.prefix	= 'snd-cat-hd';
	
	}else{
	
		this.prefix = this.prefix;
	
	}
	
	
	this.init	= function(){			
		
		for(var i = 0;i<this.$target.length;i++){		
			var t	= $(this.$target[i]);
			t.on("mouseover",$.proxy(this.addClass,this,t));
			t.on("mouseout",$.proxy(this.deleteHoverClass,this,t));			
		}		
	}
	
	this.addClass = function(e){	
		var a	= this.prefix;
		var b	= a.indexOf(this.suffix);		
		if(b < 0){		
			e.addClass(a + this.suffix);		
		}	
	}
	
	this.deleteHoverClass = function(e){		
		var a	= this.prefix + this.suffix;
		e.removeClass(a);	
	}
	
	this.resetPopPs	= function(target,tx,ty){
	
		var $t	= $(target);
		$t.css('left',tx);
		$t.css('top',ty);
	
	}

}


/*
*
*	CSS处理对象
*
*/
var resetCss	= function(){		
	
	//this.finaCss	= new Array();
	this.finaCss	= [{}];	
	
	/*
	*	设置CSS
	*/
	this.setCss	= function(id,target,paras,value){	

		var finavalue	= resetUnit(paras,value);
		var	o			= {};
		o.id			= id;
		o.str			= replaceCss(target,paras,finavalue);
		var arrindex	= this.checkCss(id);
		
		if(arrindex > 0){		

			var ro	= {};
			ro.id	= id;
			ro.str	= replaceCss(this.finaCss[arrindex].str,paras,finavalue);
			this.finaCss.splice(arrindex,1);
			this.finaCss.push(ro);
		
		}else{
		
			this.finaCss.push(o);
		
		}
		
		
		if(id == 'navh'){
		
			this.checkResetHeight(value);
		
		}
		//console.log(this.finaCss);
	
	};
	
	/*
	*	删除指定CSS
	*/
	this.deleteCss = function(id){
	
		var arrindex	= this.checkCss(id);
		this.finaCss.splice(arrindex,1);		
	
	}
	
	/*
	*
	*	创建CSS代码
	*/
	this.createCssCode = function(){
	
		var tempstr	= '';
		tempstr 	+= 	'<style type="text/css">'
		tempstr		+= 	this.getFinalCss();
		tempstr		+=	'</style>'
		$('.cssholder').html(tempstr);
		//console.log(tempstr);
		
		this.combineCss();
	
	}
	
	/*
	* 合并样式
	*/
	this.combineCss	= function(){
	
		//var a	= $('.cssholder').html(tempstr);
		//var b	= a.
		var a 	= this.deleteUnuseCss($('.resetcss').html());
		var b	= this.deleteUnuseCss($('.cssholder').html());
		var c	= a + b;
		//c	= c.replace(new RegExp(' ','g'),'');
		
		return c;
		//console.log('finacss : '+a);
	
	}
	
	/*
	* 去除样式中的多余字符
	*/
	this.deleteUnuseCss = function(a){
	
		//var s	= a.replace("/(.*)tshop-pbsm-shop-nav-ch(.*)/g","");
		var s =	a.replace(/.tshop-pbsm-shop-nav-ch/g,''); 
		s	= s.replace("<style type=\"text/css\">","");
		s	= s.replace("</style>","");	
		s	= s.replace(/^[\s]*\n/g,"");
		return s;
	
	}
	
	/*
	*	获取最终生成的css代码
	*/
	this.getFinalCss	= function(){		
		var t	= '';		
		for(var i =0;i<this.finaCss.length;i++){
			if(this.finaCss[i].str != undefined){
			
				t += this.finaCss[i].str.toString();
				
			}
		}		
		return t;	
	};
	
	/*
	*	生成样式字符串
	*/
	replaceCss = function(target,paras,value){	

		var f	= '';
		var t	= target.indexOf(paras);
		
		if(t > 0){
		
			var l	= paras.length;
			var prevstr		= target.substr(0,t);
			var afterstr	= target.substr(t).substr(target.substr(t).indexOf(';'));
			var centstr		= paras+':'+value;
			f			= prevstr	+ centstr + afterstr;
		
		}else{		

			var tempstrarr	= target.split("{");
			var prestr		= tempstrarr[0];
			var endstr		= '{' + paras + ':' +	value+';'+ tempstrarr[1];
			console.log('prev : '+prestr);
			console.log('endstr : '+tempstrarr[1]);
			f				=  prestr + endstr;			
		
		}
		
		
		
		return f;
		
	};
	
	/*
	*	检查要添加的CSS是否已经存在
	*
	*/
	this.checkCss	= function(id){			

		for(var i=0;i<this.finaCss.length;i++){
		
			var thisid	= this.finaCss[i].id;
			if(thisid	== id){
			
				return i;
			
			}

		}
		
		return false;
	
	}
	
	/*
	*	检查css单位
	*/
	resetUnit	= function(a,b){
	
		if(a == 'height' || a == 'line-height' || a == 'font-size'){
		
			return b+'px';
		
		}else{
		
			return b;
		
		}
	
	}
	
	/*
	*	当高发生变化时重置弹出层的位置
	*/

	this.checkResetHeight = function(h){	

		var ay	= parseInt($('#mainsysnav').offset().top) + parseInt(h);
		/*
		var ax	= $('.all-cats-popup').offset().left + 'px';
		menuDom.resetPopPs('.all-cats-popup',ax,ay);		
		var bx	= $('.popup-content-sub').offset().left + 'px';
		menuDom.resetPopPs('.popup-content-sub',bx,ay);
		*/
	
	}
}


/*
*
*	表单处理对象
*
*/
var FormHandLer	= function(element,obj){

	this.$target	= $(element);
	this.$btn		= $(element).find('.ui-combtn');
	this.sccid		= this.$target.attr('id');
	this.obj		= obj;
	this.value;
	
	this.init	= function(){	
		this.$btn.on("click",$.proxy(this.eventhandler,this));	
	}
	
	
	this.eventhandler	= function(e){
		
		var l	= this.obj.para.length;		
		this.value		= this.$target.find('.ui-ipttext').val();
		
		for(var i = 0;i<l;i++){
		
			var c		= this.obj.str;
			var p		= this.obj.para[i];
			var v		= this.formValueCheck(p,this.value);
			resetCss.setCss(this.sccid,c,p,v);
		
		}
		
		resetCss.createCssCode();
	
	}
	
	/*
	*	针对某些样式的值进行特殊处理
	*
	*/
	this.formValueCheck = function(a,b){
		
		var t = '';
		
		// border handler
		if(a == 'border-left' || a == 'border-right' || a == 'border' || a == 'border-top' || a == 'border-bottom'){
		
			t = this.value == ''?'0px':'1px solid '+ b;			
			
		}else{
		
			t = b; 
		
		}
		
		return t;
		
	}		

}


